import React from "react";
import {
  interaction,
  layer,
  custom,
  control, //name spaces
  Interactions,
  Overlays,
  Controls, //group
  Map,
  Layers,
  Overlay,
  Util, //objects
} from "react-openlayers";
import * as ol from "openlayers";

import { Box } from "grommet";

const MapBox = () => {
  var vectorSource = new ol.source.Cluster({
    distance: 40,
    source: new ol.source.Vector({
      url: "https://openlayers.org/en/latest/examples/kml-earthquakes.html",
      format: new ol.format.KML({
        extractStyles: false,
      }),
    }),
  });
  var cluster = new custom.style.ClusterStyle(vectorSource);
  return (
    <Box fill margin="small">
      <Map>
        <Layers>
          <layer.Tile source={new ol.source.OSM()} />
          <layer.Vector
            source={vectorSource}
            style={cluster.vectorStyleFunction}
          />
        </Layers>
        <Controls></Controls>
        <Interactions></Interactions>
        <Overlays></Overlays>
      </Map>
    </Box>
  );
};

export default MapBox;
