import React from "react";
import { Map, TileLayer, Marker, Popup } from "react-leaflet";
import "./MapBox.css";
import { Box, Text } from "grommet";

const MapBox = () => {
  const [data, setData] = React.useState([]);

  React.useEffect(() => {
    fetch(
      "https://opendata.paris.fr/api/records/1.0/search/?dataset=referentiel-archeologique-de-paris&q=&facet=code_postal&facet=nature_operation&facet=responsable_operation&facet=date_operation&facet=prehistoire&facet=protohistoire&facet=antiquite&facet=moyen_age&facet=temps_modernes&facet=epoque_contemporaine",
      {
        headers: {
          accept: "application/json",
        },
        referrer:
          "https://opendata.paris.fr/explore/dataset/referentiel-archeologique-de-paris/api/?disjunctive.code_postal&disjunctive.nature_operation&disjunctive.responsable_operation&disjunctive.date_operation&disjunctive.prehistoire&disjunctive.protohistoire&disjunctive.antiquite&disjunctive.moyen_age&disjunctive.temps_modernes&disjunctive.epoque_contemporaine",
        referrerPolicy: "strict-origin-when-cross-origin",
        body: null,
        method: "GET",
      }
    ).then((data) => setData(data.records));
  }, []);

  return (
    <Box height="medium" width="large" margin="small">
      <Map center={[48.8, 2.33]} zoom={8}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {data.map((item) => (
          <Marker position={item.fields.geo_point_2d}>
            <Popup>
              <Box margin="small">
                <Text>{item.fields.synthese}</Text>
              </Box>
            </Popup>
          </Marker>
        ))}
      </Map>
    </Box>
  );
};

export default MapBox;
