import React from "react";
import {
  interaction,
  layer,
  custom,
  control, //name spaces
  Interactions,
  Overlays,
  Controls, //group
  Map,
  Layers,
  Overlay,
  Util, //objects
} from "react-openlayers";
import * as ol from "openlayers";

import { Box } from "grommet";

const MapBox = () => {
  var vectorSource = new ol.source.Cluster({
    distance: 40,
    source: new ol.source.OSM({
      url: "https://openlayers.org/en/latest/examples/kml-earthquakes.html",
      format: new ol.format.KML({
        extractStyles: false,
      }),
    }),
  });

  var tileSource = new ol.source.Stamen({
    layer: "toner",
  });
  var cluster = new custom.style.ClusterStyle(vectorSource);
  return (
    <Box>
      <Map view={{ center: [0, 0], zoom: 2 }}>
        <Layers>
          <layer.Tile source={tileSource} />
          <layer.Vector
            source={vectorSource}
            style={cluster.vectorStyleFunction}
          />
        </Layers>
        <Controls attribution={false} zoom={true}>
          <control.Rotate />
          <control.ScaleLine />
          <control.FullScreen />
          <control.OverviewMap />
          <control.ZoomSlider />
          <control.ZoomToExtent />
          <control.Zoom />
        </Controls>
      </Map>
    </Box>
  );
};

export default MapBox;
